mapper.update:
		update Offer
     	set 
			  parkName = #{record.parkName}
			, accommodation = #{record.accommodation}
			, dtFrom = #{record.dtFrom}
			, dtTo = #{record.dtTo}
			, price = #{record.price}
		where
			id = #{record.id}

mapper.insert:
		
            select last_insert_id();
			
		insert into Offer(
			  parkName
			, accommodation
			, dtFrom
			, dtTo
			, price
		) values (
			  #{record.parkName}
			, #{record.accommodation}
			, #{record.dtFrom}
			, #{record.dtTo}
			, #{record.price}
		)
